OUTPUT="$1"
shift

set -e
exec > "$OUTPUT"

cat <<\EOF
// Autogenerated: Do not modify!
#include <map>
#include <string>
#include "examples.h"

std::map<std::string, std::string> Examples::map_ = {
EOF

for file; do

  echo "    {\"$file\", R\"FIDL("
  cat "$file"
  echo ")FIDL\"},"
done

echo '};'
